script:
employee_slip = frappe.get_all("Salary Slip" , filters={'docstatus': ['=' , 1] , 'employee': ['=' , doc.employee] , 'start_date' : ['<=' , doc.payroll_date] , 'end_date' : ['>=' , doc.payroll_date]})
if employee_slip:
    frappe.throw(_("The employee's salary has already been disbursed this month.."))

if doc.project and not doc.parent_task:  # Ensure the task is linked to a project
    total_weight = 0
    total_weight_of_project_cost = 0 

    # Get all tasks related to the same project
    # Exclude the current document if it's an existing one (to avoid double counting its current weight)
    # and then add its new weight.
    # If it's a new document, it won't have a name yet, so it will be included directly.

    # Fetch weights of existing tasks in the project
    existing_tasks = frappe.db.get_all(
        "Task",
        filters={
            "project": doc.project,
            "docstatus": 0,  # Only consider active tasks (not cancelled or submitted)
            "name": ("!=", doc.name) if doc.name else None,
            "parent_task": ["is", "not set"]
        },
        fields=["custom__weight_of_technical_achievement" , "custom_weight_of_project_cost"]
    )

    for task in existing_tasks:
        if task.custom__weight_of_technical_achievement:
            total_weight = total_weight + float(task.custom__weight_of_technical_achievement)

        if task.custom_weight_of_project_cost:
            total_weight_of_project_cost = total_weight_of_project_cost + float(task.custom_weight_of_project_cost)

    # Add the weight of the current task being saved
    if doc.custom__weight_of_technical_achievement:
        total_weight = total_weight + float(doc.custom__weight_of_technical_achievement)

    if doc.custom_weight_of_project_cost:
        total_weight_of_project_cost = total_weight_of_project_cost + float(doc.custom_weight_of_project_cost)

    # Check if the total weight exceeds 100%
    if total_weight > 100:
        frappe.throw(
            f"Total task weight for project '{doc.project}' cannot exceed 100%. "
            f"Current total: {total_weight}%."
        )

    # Check if the total weight exceeds 100%
    if total_weight_of_project_cost > 100:
        frappe.throw(
            f"Total task cost weight for project '{doc.project}' cannot exceed 100%. "
            f"Current total: {total_weight_of_project_cost}%."
        )        


if doc.parent_task:  # Ensure the task is linked to a project
    total_sub_weight = 0
    total_sub_weight_of_project_cost = 0 

    # Fetch weights of existing tasks in the project
    existing_tasks = frappe.db.get_all(
        "Task",
        filters={
            "parent_task": doc.parent_task,
            "docstatus": 0,  # Only consider active tasks (not cancelled or submitted)
            "name": ("!=", doc.name) if doc.name else None,
        },
        fields=["custom__weight_of_technical_achievement" , "custom_weight_of_project_cost"]
    )

    for task in existing_tasks:
        if task.custom__weight_of_technical_achievement:
            total_sub_weight = total_sub_weight + float(task.custom__weight_of_technical_achievement)
        if task.custom_weight_of_project_cost:
            total_sub_weight_of_project_cost = total_sub_weight_of_project_cost + float(task.custom_weight_of_project_cost)

    # Add the weight of the current task being saved
    if doc.custom__weight_of_technical_achievement:
        total_sub_weight = total_sub_weight + float(doc.custom__weight_of_technical_achievement)



    if doc.custom_weight_of_project_cost:
        total_sub_weight_of_project_cost = total_sub_weight_of_project_cost + float(doc.custom_weight_of_project_cost)

    # Check if the total weight exceeds 100%
    if total_sub_weight > 100:
        frappe.throw(
            f"Total sub task weight for task '{doc.parent_task}' cannot exceed 100%. "
            f"Current total: {total_sub_weight}%."
        )

    # Check if the total weight exceeds 100%
    if total_sub_weight_of_project_cost > 100:
        frappe.throw(
            f"Total task cost weight for project '{doc.project}' cannot exceed 100%. "
            f"Current total: {total_sub_weight_of_project_cost}%."
        )
